G.GridItemView = Marionette.View.extend({
  template: '#grid-item-tpl',

  className: 'grid-item-wrapper',

  events: {
    'click .grid-item': 'onItemClick'
  },

  active: false,

  onItemClick: function () {
    this.active ? this.deActivate() : this.makeActive()
  },

  makeActive: function () {
    var self = this
    var i = this.$el.index() + 1
    this.startIndex = i;
    this.sibl = this.$el.parent().children()
    var model = this.model

    G.itemDetails.forEach(function (detail, index, arr) {
      var inRow = G.settings.rowCount
      var left = inRow - ((i + 1) % inRow)

      if (left !== 0 && left < detail.length) {
        for(var j = 0; j < left; j++) {
          self.sibl.eq(i + j).append(self.emptyCoverEl())
        }
        i += left;
      }

      self.sibl.eq(i).append(self.coverEl(detail, model))

      if (index < arr.length - 1) {
      	i += detail.length
      } else {
      	self.endIndex =  i;
      }
      //i += detail.length
    })

    _.times(self.endIndex - self.startIndex + 1, function (n) {
      setTimeout(function () {
        self.sibl.eq(self.startIndex + n).find('.cover').addClass('on')
      }, 50 * (n))
    })

    this.active = true
  },

  deActivate: function () {
    var self = this

    _.times(this.endIndex - this.startIndex + 1, function (n) {
      console.log(self.endIndex, self.startIndex, n)
      setTimeout(function () {
        self.sibl.eq(self.endIndex - n).find('.cover').remove();
      }, 50 * (n))
    })
    this.active = false
  },

  coverEl: function (detail, model) {
    var width = detail.length * 100 +'%'
    var el = $('<div class="cover" style="width:'+ width +'"></div>')
    el.append(_.template(detail.tpl)(model.attributes))
    return el
  },

  emptyCoverEl: function () {
    return $('<div class="cover"></div>')
  },
})

G.GridView = Marionette.CollectionView.extend({
  collection: new G.ListCollection(),

  childView: G.GridItemView
})


